Json::Value Poll::toJson() const
{
    Json::Value ret;
    if(getId())
    {
        ret["id"]=getValueOfId();
    }
    else
    {
        ret["id"]=Json::Value();
    }
    if(getTitle())
    {
        ret["title"]=getValueOfTitle();
    }
    else
    {
        ret["title"]=Json::Value();
    }
    if(getSecretkey())
    {
        ret["secretkey"]=drogon::utils::base64Encode((const unsigned char *)getSecretkey()->data(),getSecretkey()->size());
    }
    else
    {
        ret["secretkey"]=Json::Value();
    }
    if(getVotes())
    {
        ret["votes"]=drogon::utils::base64Encode((const unsigned char *)getVotes()->data(),getVotes()->size());
    }
    else
    {
        ret["votes"]=Json::Value();
    }
    if(getLastsUntil())
    {
        ret["lasts_until"]=getLastsUntil()->toDbStringLocal();
    }
    else
    {
        ret["lasts_until"]=Json::Value();
    }
    if(getLastsFrom())
    {
        ret["lasts_from"]=getLastsFrom()->toDbStringLocal();
    }
    else
    {
        ret["lasts_from"]=Json::Value();
    }
    return ret;
}

